/*Write a query to get a Product list (id, name, unit price) where current products cost less than $20.*/select ProductID,ProductName,UnitPriceFrom Products where ( ( UnitPrice< 20) AND (Discontinued = 0) ) /* Write a query to get Product list (id, name, unit price) where products cost between $15 and $25  */select ProductID,ProductName,UnitPriceFrom Productswhere (UnitPrice <=25) AND (UnitPrice >=15)SELECT ProductID,ProductName,UnitPrice FROM ProductsWHERE UnitPrice BETWEEN 15 AND 25;/*	 Write a query to get Product list (name, unit price) of above average price. */SELECT ProductName,UnitPriceFROM Products WHERE (UnitPrice ) > (select AVG(UnitPrice) as avg from Products )/*	Write a query to get Product list (name, unit price) of ten most expensive product  */ select Top 10  ProductName,UnitPrice from   Products order by UnitPrice desc ;  /* 	Write a query to count current and discontinued products  */  SELECT Discontinued,COUNT(ProductID) as "count"   from Products  group by Discontinued;       /* 		Write a query to get Product list (name, units on order , units in stock) of stock is less than the quantity on order   */   select ProductName,UnitsOnOrder,UnitsInStock from Products p   where p.UnitsInStock < p.UnitsOnOrder ;   